mapper.update:
UPDATE sys_user SET deleted=1 
WHERE deleted=0 AND
	id = #{value}

UPDATE sys_user
account_id = #{accountId}, name = #{name}, sex = #{sex}, age = #{age}, Column_12 = #{column12}, telephone = #{telephone}, mobilephone = #{mobilephone}, job = #{job}, qq = #{qq}, wechat = #{wechat}, microblog = #{microblog}, email = #{email}, office_addr = #{officeAddr}, ctime = #{ctime}, creater = #{creater}, creater_id = #{createrId}, etime = #{etime}, editor = #{editor}, editor_id = #{editorId}, comments = #{comments}, deleted = #{deleted},
WHERE deleted=0 AND id = #{id}

